class Solution {
  public List<Integer> intersection(List<List<Integer>> lists) {
    // Complexity: O(m * n)
    // Here m is the number of sublists and n is the number of elements in the sublists.
    Map<Integer, Integer> counts = new HashMap<>();
    Set<Integer> result = new HashSet<>();
    // Traverses every sublist and count the occurrence of each element.
    for (List<Integer> list: lists) {
      for (Integer num: list) {
        counts.put(num, counts.getOrDefault(num, 0) + 1);
      }
    }
    // Checks the highest frequency and adds them to result set.
    for (Map.Entry<Integer, Integer> entry: counts.entrySet()) {
      if (entry.getValue() == lists.size()) {
        result.add(entry.getKey());
      }
    }
    return new ArrayList<>(result);
  }
}

