def teacher_roster(student_names, instructor_names):
    # Check if both input lists are empty
    if not student_names and not instructor_names:
        return {}

    # Check if there are no students or no instructors
    if not student_names:
        return {student_name: "" for student_name in student_names}

    if not instructor_names:
        return {instructor_name: "" for instructor_name in instructor_names}

    # Check if there are more students than instructors
    if len(student_names) > len(instructor_names):
        extra_students = len(student_names) - len(instructor_names)
        instructor_names += instructor_names[:extra_students]

    # Check if there are more instructors than students
    if len(student_names) < len(instructor_names):
        extra_instructors = len(instructor_names) - len(student_names)
        instructor_names = instructor_names[:len(student_names)]

    # Pair each student name with an instructor name
    roster = {student_name: instructor_name for student_name, instructor_name in zip(student_names, instructor_names)}

    return roster
