        public static void QuickSort(int[] array, int lower, int upper)
        {
            if (lower < upper)
            {
                int partitionIndex = Partition(array, lower, upper);
                QuickSort(array, lower, partitionIndex - 1);
                QuickSort(array, partitionIndex + 1, upper);
            }
        }

        private static int Partition(int[] array, int lower, int upper)
        {
            int pivot = array[lower + (upper - lower) / 2];
            while (true)
            {
                while (array[lower] < pivot)
                    lower++;
                while (array[upper] > pivot)
                    upper--;
                if (lower <= upper)
                {
                    int tmp = array[lower];
                    array[lower] = array[upper];
                    array[upper] = tmp;
                    lower++;
                    upper--;
                }
                if (lower >= upper)
                    return lower;
            }
        }
    }
}

