# Python program to check security of password

def check_security(password):
    # Minimum length of password should be 8
    if len(password) < 8:
        return False

    # Password should contain at least one uppercase
    if not any(x.isupper() for x in password):
        return False

    # Password should contain at least one lowercase
    if not any(x.islower() for x in password):
        return False

    # Password should contain at least one special character
    if not any(x in "!@#$%^&*()_+{}|:<>?/.,~`-=" for x in password):
        return False

    # Password should contain at least one number
    if not any(x.isdigit() for x in password):
        return False

    return True

