def galaxy_package_name(galaxy_name, store, name, version=None, driver=None, install_from=None, roles_directory=None, sha1=None):
    '''
    Build a package name for a galaxy.
    '''

    install_from = _guess_install_from(galaxy_name, install_from)

    if install_from is None and sha1:
        raise ValueError('Cannot build package name with a sha1 and not a install_from')

    role_path = '' if roles_directory is None else os.path.join(roles_directory, name)
    if install_from:
        if not _is_supported_install_from(install_from):
            raise ValueError('Unsupported install_from')

        install_from_type = _install_from_type(install_from)
        if install_from_type == 'git':
            if version:
                # Git repository without tag / hash always get it as the name
                name = name.rsplit('/', 1)[-1]
                if not name:
                    raise ValueError('Galaxy name has a slash but the name is empty.')
            version = version or 'latest'

            # Prevent as the automatic version always triggers a reinstall
            if not name and version == 'latest':
                raise ValueError('Galaxy name with git install_from and latest version cannot be empty')

            if version == 'latest':
                return '{name}--{driver}'.format(name=name, driver=driver)

            return '{name}--{type}--{version}--{driver}'.format(name=name, type=install_from_type, version=version, driver=driver)

        if version:
            raise ValueError('Version is only relevant for git repositories.')

        return '{name}--{type}--{driver}'.format(name=name, type=install_from_type, driver=driver)

    if sha1:
        if install_from:
            raise ValueError('SHA1 is incompatible with install_from')
        return '{name}--{sha1}'.format(name=name, sha1=sha1)

    if not version:
        version = latest_version(galaxy_name, store)

    return '{name}--{version}--{driver}'.format(name=name, version=version, driver=driver)

