    parenthesis_count = {'(': 0, ')': 0}

    for i in input_string:
        # Check if current character is an opening parenthesis
        if i == '(':
            parenthesis_count['('] += 1
        # Check if current character is a closing parenthesis
        elif i == ')':
            parenthesis_count[')'] += 1
            # If there are more closing parenthesis than opening parenthesis
