@RestController
@EnableResourceServer
public class TokenController {
    private final ResourceServerTokenServices tokenServices;
    public TokenController(ResourceServerTokenServices tokenServices) {
        this.tokenServices = tokenServices;
    }
    @PostMapping("/token")
    public Object createAccessToken(@RequestBody LoginRequest loginRequest) {
        OAuth2ClientContext clientContext = new DefaultOAuth2ClientContext();
        ResourceOwnerPasswordResourceDetails resourceDetails = new ResourceOwnerPasswordResourceDetails();
        resourceDetails.setClientId(loginRequest.getClientId());
        resourceDetails.setClientSecret(loginRequest.getClientSecret());
        resourceDetails.setAccessTokenUri("http://localhost:8080/oauth/token");
        resourceDetails.setUsername(loginRequest.getUsername());
        resourceDetails.setPassword(loginRequest.getPassword());
        OAuth2RestTemplate restTemplate = new OAuth2RestTemplate(resourceDetails, clientContext);
        OAuth2ClientContext context = restTemplate.getOAuth2ClientContext();
        context.setAccessToken(tokenServices.createAccessToken(restTemplate.getAccessToken()));
        return restTemplate.getAccessToken();
    }
}
