def quicksort(arr):
    if len(arr) <= 1:
        return arr
    else:
        left, right, equal = [], [], []
        pivot = arr[0]
        for i in arr:
            if i < pivot:
                left.append(i)
            elif i > pivot:
                right.append(i)
            else:
                equal.append(i)
        return quicksort(left) + equal + quicksort(right)

