        // Check whether the string contains an @ symbol
        if (strpos($email, '@') === false) {
            return false;
        }
        
        // Split the string into username and domain parts
        list($username, $domain) = explode('@', $email);
        
        // Check whether the username and domain are non-empty strings
        if (empty($username) || empty($domain)) {
            return false;
        }
        
        // Check whether the domain contains a dot and at least two letters
        if (!preg_match('/[a-zA-Z]{2,4}(\.[a-zA-Z]{2,4}){1,}/', $domain)) {
            return false;
        }
        
        // Check whether the username contains at least two characters and no special characters
        if (strlen($username) < 2 || !preg_match('/^[a-zA-Z0-9_]+$/', $username)) {
            return false;
        }
        
        // Check whether the username and domain are not blacklisted
