    primes = [True] * n
    primes[0], primes[1] = False, False

    p = 2
    while p * p <= n:
        if primes[p] is True:
            for i in range(p * 2, n+1, p):
                primes[i] = False
        p += 1

    prime_list = [p for p in range(n) if primes[p]]
