    this.container.innerHTML = '<h2>Calendar</h2>';

    // Generate day headers
    const headers = document.createElement('div');
    headers.classList.add('headers');

    for (let i = 0; i < 7; i++) {
      const header = document.createElement('h3');
      header.textContent = `Day ${i + 1}`;
      headers.appendChild(header);
    }

    this.container.appendChild(headers);

    // Generate months
    const months = document.createElement('div');
    months.classList.add('months');

    for (let i = 0; i < 12; i++) {
      const month = document.createElement('div');
      month.classList.add('month');
      const monthYear = `${i + 1} ${new Date().getFullYear()}`;

      month.appendChild(document.createTextNode(monthYear));
      months.appendChild(month);
    }

    this.container.appendChild(months);
  }
}

