def calculate(expr):
    """This program calculates the result of a mathematical expression."""
    def helper(expr):
        """This helper function calculates the result of a sub-expression."""
        try:
            head, *tail = expr
            if head == '+':
                return sum(helper(t) for t in tail)
            elif head == '-':
                return helper(tail[0]) - sum(helper(t) for t in tail[1:])
            elif head == '*':
                return helper(tail[0]) * sum(helper(t) for t in tail[1:])
            elif head == '/':
                return helper(tail[0]) / sum(helper(t) for t in tail[1:])
        except ZeroDivisionError:
            return 0

    return helper(expr)

