class FIFOQueue:
    def __init__(self):
        self.items = []

    # Add element to the queue
    def enqueue(self, item):
        self.items.insert(0, item)

    # Remove the front element
    def dequeue(self):
        return self.items.pop()

    # Check if the queue is empty
    def is_empty(self):
        return self.items == []

