class Stack(object):
    def __init__(self, capacity):
    self.capacity = capacity

    def push(self, item):
    if len(self) == self.capacity:
    raise Exception("Stack is full")
    self.data.append(item)
    return True

    def pop(self):
    if len(self) == 0:
    raise Exception("Stack is empty")
    return self.data.pop()

