    return []
    elif user.rating >= 4:
    return [song for song in songs if user.rating > song.rating]
    else:
    return [song for song in songs if user.rating < song.rating]

class Solution(object):
   def shortestPath(self, start, end):
        if start == end:
        return []
        path = [start]
        while path[-1] != end:
            new_path = path + [path[-1]]
            if new_path[0] < end:
            path = new_path
            else:
            path.append(new_path[0])
            return path

